// Validation et normalisation de l'entrée
const input = $input.item.json;

// Supporter plusieurs formats d'entrée
let body = input.body || input;
let message = body.message || input.message;

// Validation
if (!message || typeof message !== 'string' || message.trim() === '') {
  return {
    json: {
      success: false,
      error: 'Le champ "message" est requis et doit être une chaîne de caractères',
      errorCode: 'MISSING_MESSAGE',
      timestamp: new Date().toISOString(),
      _validationFailed: true
    }
  };
}

// Normaliser les données
return {
  json: {
    message: message.trim(),
    timestamp: body.timestamp || new Date().toISOString(),
    file: body.file || input.file || null,
    metadata: body.metadata || {},
    isValid: true
  }
};

// Détection intelligente du type d'entrée
const input = $input.item.json;
const message = input.message || '';

// Variables de sortie
let inputType = 'text';
let command = null;
let prompt = message;

// Détection des commandes
const imagePatterns = ['/image ', '/img ', '/gen '];
const searchPatterns = ['/search ', '/find ', '/cherche '];
const videoPatterns = ['/video ', '/vid '];

for (const pattern of imagePatterns) {
  if (message.toLowerCase().startsWith(pattern)) {
    inputType = 'image-generation';
    command = 'image';
    prompt = message.substring(pattern.length).trim();
    break;
  }
}

if (inputType === 'text') {
  for (const pattern of searchPatterns) {
    if (message.toLowerCase().startsWith(pattern)) {
      inputType = 'web-search';
      command = 'search';
      prompt = message.substring(pattern.length).trim();
      break;
    }
  }
}

if (inputType === 'text') {
  for (const pattern of videoPatterns) {
    if (message.toLowerCase().startsWith(pattern)) {
      inputType = 'video-generation';
      command = 'video';
      prompt = message.substring(pattern.length).trim();
      break;
    }
  }
}

// Détection de fichier
if (input.file) {
  inputType = 'file-analysis';
  command = 'analyze';
}

return {
  json: {
    ...input,
    inputType: inputType,
    command: command,
    prompt: prompt,
    originalMessage: message,
    hasFile: !!input.file,
    fileType: input.file?.type || null,
    fileName: input.file?.name || null
  }
};

// Formater la réponse DALL-E
const input = $input.item.json;
const previousData = $('Detect Input Type').item.json;

// Extraire l'URL de l'image de la réponse DALL-E
const imageUrl = input.data?.[0]?.url || null;
const revisedPrompt = input.data?.[0]?.revised_prompt || previousData.prompt;

if (!imageUrl) {
  return {
    json: {
      success: false,
      type: 'error',
      content: {
        message: 'Erreur lors de la génération de l\'image',
        error: input.error?.message || 'URL d\'image non trouvée'
      },
      metadata: {
        inputType: previousData.inputType,
        command: previousData.command,
        originalMessage: previousData.originalMessage
      },
      timestamp: new Date().toISOString()
    }
  };
}

return {
  json: {
    success: true,
    type: 'image',
    content: {
      url: imageUrl,
      description: revisedPrompt,
      originalPrompt: previousData.prompt
    },
    metadata: {
      inputType: previousData.inputType,
      command: previousData.command,
      originalMessage: previousData.originalMessage,
      model: 'dall-e-3'
    },
    timestamp: new Date().toISOString()
  }
};

// Formater la réponse vidéo Replicate
const input = $input.item.json;
const previousData = $('Detect Input Type').item.json;

// Vérifier le statut
const status = input.status;
const videoUrl = input.output;

// Si la vidéo n'est pas prête
if (status !== 'succeeded' || !videoUrl) {
  return {
    json: {
      success: false,
      type: 'info',
      content: {
        message: `Génération vidéo en cours... Status: ${status}`,
        instructions: 'La vidéo prend 30-60 secondes à générer. Veuillez patienter ou réessayer.',
        status: status,
        prompt: previousData.prompt
      },
      metadata: {
        inputType: previousData.inputType,
        command: previousData.command,
        originalMessage: previousData.originalMessage
      },
      timestamp: new Date().toISOString()
    }
  };
}

// Succès - vidéo générée
return {
  json: {
    success: true,
    type: 'video',
    content: {
      url: videoUrl,
      description: previousData.prompt,
      duration: 3.5  // Environ 25 frames à 7 fps
    },
    metadata: {
      inputType: previousData.inputType,
      command: previousData.command,
      originalMessage: previousData.originalMessage,
      model: 'stable-video-diffusion',
      provider: 'replicate'
    },
    timestamp: new Date().toISOString()
  }
};

// Placeholder pour analyse de fichiers
const input = $input.item.json;

return {
  json: {
    success: true,
    type: 'info',
    content: {
      message: `Analyse de fichier demandée: "${input.fileName}". Fonctionnalité à configurer.`,
      instructions: 'Pour activer : Ajoutez GPT-4 Vision ou Claude Vision API',
      fileName: input.fileName,
      fileType: input.fileType
    },
    metadata: {
      inputType: input.inputType,
      command: input.command,
      originalMessage: input.originalMessage
    },
    timestamp: new Date().toISOString()
  }
};

// Formatage unifié de toutes les réponses
const input = $input.item.json;

// Si déjà formaté correctement
if (input.success !== undefined && input.type && input.content) {
  return { json: input };
}

// Déterminer le type de réponse
let responseType = 'text';
let content = input.output || input.text || input.content || input;
let success = true;

// Réponse AI Agent
if (input.output) {
  responseType = 'text';
  content = input.output;
}

// Image
if (input.imageUrl || input.image_url) {
  responseType = 'image';
  content = {
    url: input.imageUrl || input.image_url,
    description: input.description || input.prompt || 'Image générée'
  };
}

// Vidéo
if (input.videoUrl || input.video_url) {
  responseType = 'video';
  content = {
    url: input.videoUrl || input.video_url,
    description: input.description || input.prompt || 'Vidéo générée'
  };
}

// Audio
if (input.audioUrl || input.audio_url) {
  responseType = 'audio';
  content = {
    url: input.audioUrl || input.audio_url,
    description: input.description || 'Audio généré'
  };
}

// Erreur
if (input.type === 'error' || input.error) {
  responseType = 'error';
  success = false;
  content = {
    message: input.error || input.message || 'Erreur inconnue',
    errorCode: input.errorCode || 'UNKNOWN_ERROR'
  };
}

return {
  json: {
    success: success,
    type: responseType,
    content: content,
    metadata: {
      inputType: input.inputType || 'text',
      originalMessage: input.originalMessage || input.message || '',
      processingTime: input.processingTime || null,
      model: input.model || 'claude-sonnet-4.5',
      timestamp: input.timestamp || new Date().toISOString()
    },
    timestamp: new Date().toISOString()
  }
};
